SELECT IFNULL( (SELECT SUM(req_money) FROM give_balance_history AS g JOIN hierarchy AS h ON g.sender_id = h.user_id JOIN system_tree AS gt ON g.sender_id = gt.user_id
WHERE node_id LIKE CONCAT(#{node_id},'%') AND date(g.event_time) = curdate()
), 0) AS 당일지급, IFNULL( (SELECT SUM(req_money) FROM take_balance_history AS t JOIN hierarchy AS h ON t.sender_id = h.user_id JOIN system_tree AS tt ON t.sender_id = tt.user_id
WHERE node_id LIKE CONCAT(#{node_id},'%') AND date(t.event_time) = curdate()
), 0) AS 당일회수

