SELECT u.level as '레벨', u.type as '타입', u.id as '아이디', u.nickname '닉네임', u2.nickname as '상위 에이전트', a.slot_balance + a.casino_balance '보유금', a.point '포인트', b.c_today_betting '카_베팅', b.c_today_win '카_윈', b.s_today_betting '슬_베팅', b.s_today_win '슬_윈', b.c_temp_betting '카_임시베팅', b.c_temp_betting * c.c_roll_rate / 100 '카_임시롤링', b.s_temp_betting '슬_임시베팅', b.s_temp_betting * c.s_roll_rate / 100 '슬_임시롤링', u.join_code as '가입코드', u.user_id, u.api_type AS 'API타입' FROM user AS u JOIN user AS u2 ON u.upper_agt = u2.user_id JOIN user_asset_info AS a ON u.user_id = a.user_id JOIN user_betting_info AS b ON u.user_id = b.user_id JOIN user_commission_info AS c ON u.upper_agt = c.user_id JOIN system_tree AS t ON u.upper_agt = t.user_id WHERE u.type = 4 AND u.id IN
#{id}
AND node_id = #{node_id}
AND (node_id = #{node_id} OR node_id LIKE CONCAT(#{node_id},'.%'))

UPDATE user_betting_info
SET c_temp_betting = 0, s_temp_betting = 0
WHERE user_id = #{user_id}
