mapper.select:
SELECT d.hl_detail_log_id AS IDX, d.event_date AS 발생시간, d.user_id AS 아이디, d.transaction_id AS 트랜젝션ID, d.round_id AS 라운드ID, d.provider_name AS 프로바이더, d.category AS 게임타입, g.title AS 게임명, d.transaction_type AS 내역타입, d.transaction_amount AS 금액, d.previous_balance AS 이전보유금, d.available_balance AS 이후보유금, t.node_id, u.type AS 회원타입, u.join_code AS 가입코드 FROM log_hl_betting_detail AS d JOIN wawa.user AS u ON u.id = d.user_id JOIN wawa.game_list AS g ON d.provider_name = g.provider AND d.game_id = g.uuid JOIN wawa.system_tree AS t ON u.upper_agt = t.user_id JOIN wawa.user AS u2 on u.upper_agt = u2.user_id
SUBSTR(event_date, 1, 16) BETWEEN #{startDate} AND #{endDate} AND u.id = #{userId}
AND ( d.user_id LIKE CONCAT('%', #{searchValue}, '%') OR d.transaction_id LIKE CONCAT('%', #{searchValue}, '%') OR d.round_id LIKE CONCAT('%', #{searchValue}, '%') OR d.provider_name LIKE CONCAT('%', #{searchValue}, '%') OR d.category LIKE CONCAT('%', #{searchValue}, '%') OR g.title LIKE CONCAT('%', #{searchValue}, '%') OR d.transaction_type LIKE CONCAT('%', #{searchValue}, '%') OR d.transaction_amount LIKE CONCAT('%', #{searchValue}, '%') OR d.previous_balance LIKE CONCAT('%', #{searchValue}, '%') OR d.available_balance LIKE CONCAT('%', #{searchValue}, '%') )
ORDER BY ${orderColumn} ${orderDirection} LIMIT ${pageSize} OFFSET ${offset}
